function emojis()
{
	$str = '';
	$rand = rand(1,2);
	for($i = 0; $i < $rand; $i ++)
	{
		$str .= emoji();
	}
	$str .= rand(10,99);
	if(time() % 10 < 4)
	{
		$str .= emoji();
	}elseif(time() % 10 < 7){
		$str .= emoji() . emoji();
	}
	return $str;
}
